import React, { useState, useRef, useEffect } from "react";
import axios from "axios";

function App() {
  const [messages, setMessages] = useState([
    { sender: "bot", text: "🌟 Hello! I'm your advanced AI assistant. I'm here to help you with questions, have conversations, and assist with various topics. How can I help you today?" }
  ]);
  const [input, setInput] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isOnline, setIsOnline] = useState(true);
  const messagesEndRef = useRef(null);
  const API_URL = process.env.REACT_APP_API_URL ? `${process.env.REACT_APP_API_URL}/chat` : "http://127.0.0.1:8000/chat";

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim() || isLoading) return;

    const userMsg = { sender: "user", text: input, timestamp: new Date().toLocaleTimeString() };
    setMessages((prev) => [...prev, userMsg]);
    setInput("");
    setIsLoading(true);

    try {
      const res = await axios.post(API_URL, { message: input });
      const botMsg = { 
        sender: "bot", 
        text: res.data.response || "I'm sorry, I didn't understand that. Could you please rephrase your question?", 
        timestamp: new Date().toLocaleTimeString()
      };
      setMessages((prev) => [...prev, botMsg]);
      setIsOnline(true);
    } catch (err) {
      console.error("Error:", err);
      const errorMsg = { 
        sender: "bot", 
        text: "🔧 I'm having trouble connecting right now. Please make sure the backend server is running on port 8000 and try again. If the issue persists, try restarting the backend server.", 
        timestamp: new Date().toLocaleTimeString()
      };
      setMessages((prev) => [...prev, errorMsg]);
      setIsOnline(false);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      sendMessage();
    }
  };

  const clearChat = () => {
    setMessages([
      { sender: "bot", text: "🌟 Hello! I'm your advanced AI assistant. I'm here to help you with questions, have conversations, and assist with various topics. How can I help you today?" }
    ]);
  };

  const suggestedQuestions = [
    "What's the weather like?",
    "Tell me a joke",
    "How does AI work?",
    "What can you help me with?"
  ];

  const handleSuggestedQuestion = (question) => {
    setInput(question);
  };

  return (
    <div className="flex flex-col h-screen bg-gradient-to-br from-indigo-50 via-white to-cyan-50">
      {/* Enhanced Header */}
      <div className="bg-white/80 backdrop-blur-lg shadow-xl border-b border-gray-200/50">
        <div className="max-w-6xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="w-12 h-12 bg-gradient-to-br from-blue-500 via-purple-500 to-pink-500 rounded-2xl flex items-center justify-center shadow-lg">
                  <span className="text-white font-bold text-xl">🤖</span>
                </div>
                <div className={`absolute -bottom-1 -right-1 w-4 h-4 rounded-full border-2 border-white ${
                  isOnline ? 'bg-green-400' : 'bg-red-400'
                }`}></div>
              </div>
              <div>
                <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
                  AI Chat Assistant
                </h1>
                <p className="text-sm text-gray-600 flex items-center space-x-2">
                  <span>Powered by DialoGPT</span>
                  <span className="w-1 h-1 bg-gray-400 rounded-full"></span>
                  <span className={isOnline ? 'text-green-600' : 'text-red-600'}>
                    {isOnline ? 'Online' : 'Offline'}
                  </span>
                </p>
              </div>
            </div>
            <button
              onClick={clearChat}
              className="px-4 py-2 bg-gradient-to-r from-gray-100 to-gray-200 hover:from-gray-200 hover:to-gray-300 text-gray-700 rounded-xl transition-all duration-200 shadow-sm hover:shadow-md"
            >
              🗑️ Clear Chat
            </button>
          </div>
        </div>
      </div>

      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto px-4 py-6 bg-gradient-to-b from-transparent to-gray-50/30">
        <div className="max-w-5xl mx-auto space-y-6">
          {messages.length === 1 && (
            <div className="text-center py-8">
              <div className="mb-6">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Quick Start Questions</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-3 max-w-2xl mx-auto">
                  {suggestedQuestions.map((question, index) => (
                    <button
                      key={index}
                      onClick={() => handleSuggestedQuestion(question)}
                      className="p-3 bg-white/70 hover:bg-white/90 border border-gray-200 rounded-xl text-sm text-gray-700 hover:shadow-md transition-all duration-200 hover:scale-105"
                    >
                      {question}
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}

          {messages.map((msg, i) => (
            <div
              key={i}
              className={`flex ${msg.sender === "user" ? "justify-end" : "justify-start"} animate-fadeIn`}
            >
              <div className={`flex max-w-2xl ${msg.sender === "user" ? "flex-row-reverse" : "flex-row"} items-end space-x-3`}>
                {msg.sender === "bot" && (
                  <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center flex-shrink-0 shadow-lg">
                    <span className="text-white text-lg">🤖</span>
                  </div>
                )}
                <div className="flex flex-col space-y-1">
                  <div
                    className={`px-6 py-4 rounded-3xl shadow-lg backdrop-blur-sm ${
                      msg.sender === "user"
                        ? "bg-gradient-to-r from-blue-500 to-blue-600 text-white rounded-br-lg"
                        : "bg-white/90 text-gray-800 rounded-bl-lg border border-gray-100/50"
                    }`}
                  >
                    <p className="text-sm leading-relaxed whitespace-pre-wrap">{msg.text}</p>
                  </div>
                  {msg.timestamp && (
                    <span className={`text-xs text-gray-500 ${msg.sender === "user" ? "text-right" : "text-left"}`}>
                      {msg.timestamp}
                    </span>
                  )}
                </div>
                {msg.sender === "user" && (
                  <div className="w-10 h-10 bg-gradient-to-br from-emerald-400 to-teal-500 rounded-2xl flex items-center justify-center flex-shrink-0 shadow-lg">
                    <span className="text-white text-lg">👤</span>
                  </div>
                )}
              </div>
            </div>
          ))}
          
          {isLoading && (
            <div className="flex justify-start animate-fadeIn">
              <div className="flex items-end space-x-3">
                <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center shadow-lg">
                  <span className="text-white text-lg">🤖</span>
                </div>
                <div className="bg-white/90 px-6 py-4 rounded-3xl rounded-bl-lg border border-gray-100/50 shadow-lg">
                  <div className="flex space-x-2">
                    <div className="w-3 h-3 bg-blue-400 rounded-full animate-bounce"></div>
                    <div className="w-3 h-3 bg-purple-400 rounded-full animate-bounce" style={{ animationDelay: "0.1s" }}></div>
                    <div className="w-3 h-3 bg-pink-400 rounded-full animate-bounce" style={{ animationDelay: "0.2s" }}></div>
                  </div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Enhanced Input Area */}
      <div className="bg-white/80 backdrop-blur-lg border-t border-gray-200/50 p-6 shadow-xl">
        <div className="max-w-5xl mx-auto">
          <div className="flex space-x-4">
            <div className="flex-1 relative">
              <div className="relative">
                <textarea
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask me anything... (Press Enter to send, Shift+Enter for new line)"
                  className="w-full px-6 py-4 pr-16 bg-white/90 border border-gray-200/50 rounded-2xl focus:outline-none focus:ring-2 focus:ring-blue-500/50 focus:border-blue-500/50 resize-none shadow-inner backdrop-blur-sm text-gray-800 placeholder-gray-500"
                  rows="1"
                  style={{ minHeight: "56px", maxHeight: "120px" }}
                  disabled={isLoading}
                />
                <button
                  onClick={sendMessage}
                  disabled={!input.trim() || isLoading}
                  className="absolute right-2 top-1/2 transform -translate-y-1/2 p-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-xl hover:from-blue-600 hover:to-purple-700 disabled:opacity-50 disabled:cursor-not-allowed transition-all duration-200 shadow-lg hover:shadow-xl hover:scale-105"
                >
                  <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
                  </svg>
                </button>
              </div>
              <div className="flex items-center justify-between mt-3">
                <p className="text-xs text-gray-500 flex items-center space-x-2">
                  <span>💡 Tip: Ask me about anything!</span>
                  <span>•</span>
                  <span>AI responses may take a moment</span>
                </p>
                <div className="flex items-center space-x-2 text-xs text-gray-500">
                  <span className="flex items-center space-x-1">
                    <div className={`w-2 h-2 rounded-full ${isOnline ? 'bg-green-400' : 'bg-red-400'}`}></div>
                    <span>{isOnline ? 'Connected' : 'Disconnected'}</span>
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
